import React from 'react';

import AddressCard from '../Card/Card';
import './List.css';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const AddressList = ({ loading, address }) => {
  if (loading){
    return <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>Carregando...</div>
  }

  return (
    <div className='address-list'>
      {address.map((address) => (
        <AddressCard 
          address={address} 
          key={address.id} 
          onClickDelete={async () => { 
            await axios.delete(`http://localhost:5000/address/${address.id}`)
            useHistory.push('/');
         } } />  
      ))}
    </div>
  )
}

export default AddressList;
