import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

import AddressList from '../List/List';
import './Search.css';

const AddressSearch = () => {
  const [atributte, setAtributte] = useState('');
  const [address, setAddress] = useState([]);
  const [search, setSearch] = useState('');

  useEffect(() => {
    const params = {};
    if (search){
      params.nome_like = search;
    }
    axios.get('http://localhost:5000/address', {params})
    .then((response) => {
      setAddress(response.data);
    });
  }, [search]);

  return (
    <div className='address-search'>
      <header className='address-search__header'>
        <h1>Endereços</h1>
        <Link to="/create">Novo endereço</Link>
      </header>

      <div className='address-search__atributtes'>
        <input 
          className='address-search__input' 
          type='search' 
          placeholder="Buscar"
          value={search}
          onChange={(ev) => setSearch(ev.target.value)}
        />

        <select className='address-search__select' id='atribute'>
          <option value='nome'>Nome</option>
          <option value='cep'>CEP</option>
          <option value='rua'>Rua</option>
          <option value='Bairro'>Bairro</option>
        </select>
      </div>

      <AddressList address={address} loading={!address.length} />

    </div>
  )
}

export default AddressSearch;
