import React, { useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

import './Form.css';

const initialValue = {
  cep: '',
  rua: '',
  numero: '',
  bairro: '',
  cidade: '',
  estado: '',
  complemento: '',
  ponto_referencia: '',
}

const AddressForm = ({ id }) => {
  const [values, setValues] = useState(initialValue)
  const history = useHistory();

  console.log(id);

  function onSubmit(ev) {
    ev.preventDefault();

    axios.post('http://localhost:5000/address', values)
      .then((response) => {
        history.push('/');
      })

    console.log(values);
  }

  function onChange(ev) {
    const { name, value } = ev.target;

    setValues({...values, [name]: value});
  }

  return (
    <div>
      <h1>Novo endereco</h1>

      <form onSubmit={onSubmit}>
        <div className='address-form__group'>
          <label htmlFor='nome'>Nome</label>
          <input id='nome' name='nome' onChange={onChange}/>
          <label htmlFor='cep'>CEP</label>
          <input id='cep' name='cep' onChange={onChange}/>
          <label htmlFor='rua'>Rua</label>
          <input id='rua' name='rua' onChange={onChange} />
          <label htmlFor='numero'>Número</label>
          <input id='numero' name='numero' onChange={onChange} />                  
          <label htmlFor='bairro'>Bairro</label>
          <input id='bairro' name='bairro' onChange={onChange} />
          <label htmlFor='cidade'>Cidade</label>
          <input id='cidade' name='cidade' onChange={onChange} />
          <label htmlFor='estado'>Estado</label>
          <input id='estado' name='estado' onChange={onChange} />
          <label htmlFor='complemento'>Complemento</label>
          <input id='complemento' name='complemento' onChange={onChange} />
          <label htmlFor='rua'>Ponto de referência</label>
          <input id='ponto_referencia' name='ponto_referencia' onChange={onChange} />
          <div>
            <button type='submit'>Salvar</button>
          </div>
        </div>
      </form>
    </div>
  )
}

export default AddressForm;
