import React from 'react';

import AddressCard from '../Card/Card';
import './List.css';

const AddressList = ({ loading, address }) => {
  if (loading){
    return <div style={{
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
    }}>Carregando...</div>
  }

  return (
    <div className='address-list'>
      {address.map((address) => (
        <AddressCard address={address} key={address.cep}/>  
      ))}
    </div>
  )
}

export default AddressList;
